    private void JBCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBCalcularActionPerformed
        double n1, n2, media;
        
        n1 = Double.parseDouble(JTFN1.getText());
        n2 = Double.parseDouble(JTFN2.getText());
        media = (n1 + n2) / 2;
        JLMedia.setText(String.valueOf(media));
        
        if (media > 6)    {
            JTFNome.setText(String.valueOf("Aprovado"));    }
        else    {
            JTFNome.setText(String.valueOf("Reprovado"));   } 
    }//GEN-LAST:event_JBCalcularActionPerformed

